public class largestOfThreeNumbers {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a = 1022;
		int b =112;
		int c =1222;
		
		if((a>b)&(a>c)) {
			System.out.println("a is greater");
		}
		else if( b > c) {
			System.out.println("b is greater");
		}
		else {
			System.out.println("c is greatest");
		}

	}

}
