package naveenAutomationPractice;

/**
 * @author sami
 *
 */
public class multiplicationTable {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		int num = 2;
		int mul  = 1;
		
		for(int i = 1; i<=10;i++) {
			
			mul = num*i;
			System.out.println(num+"x"+i+"="+mul);
		
					
		}

	}

}
