public class quotientAndReminder {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 11;
		int div = 2;
		int q = num/div;
		int rem = num%div;
		System.out.println(q);
		System.out.println(rem);

	}

}
